import { createStore } from 'vuex'
import { fetchNewsList } from '@/api'
export default createStore({
  state: {
    news: []
  },
  getters: {},
  mutations: {},
  actions: {
    FETCH_NEWS() {
      fetchNewsList().then((response) => {
        console.log(response)
      })
    }
  },
  modules: {}
})
