import { createStore } from 'vuex'
import { fetchNewsList } from '@/api'
export default createStore({
  state: {
    news: []
  },
  getters: {},
  mutations: {
    SET_NEWS(state, data) {
      state.news = data
    }
  },
  actions: {
    FETCH_NEWS(context) {
      fetchNewsList()
        .then((response) => {
          console.log(response.data)
          context.commit('SET_NEWS', response.data)
        })
        .catch((error) => {
          console.log(error)
        })
    }
  },
  modules: {}
})
